// HasClue applies the HasEdge predicate on the "clue" edge.
func HasClue() predicate.Novel {
	return predicate.Novel(func(s *sql.Selector) {
		step := sqlgraph.NewStep(
			sqlgraph.From(Table, FieldID),
			sqlgraph.To(ClueTable, FieldID),
			sqlgraph.Edge(sqlgraph.O2M, false, ClueTable, ClueColumn),
		)
		sqlgraph.HasNeighbors(s, step)
	})
}

// HasClueWith applies the HasEdge predicate on the "clue" edge with a given conditions (other predicates).
func HasClueWith(preds ...predicate.NovelClue) predicate.Novel {
	return predicate.Novel(func(s *sql.Selector) {
		step := sqlgraph.NewStep(
			sqlgraph.From(Table, FieldID),
			sqlgraph.To(ClueInverseTable, FieldID),
			sqlgraph.Edge(sqlgraph.O2M, false, ClueTable, ClueColumn),
		)
		sqlgraph.HasNeighborsWith(s, step, func(s *sql.Selector) {
			for _, p := range preds {
				p(s)
			}
		})
	})
}

// And groups predicates with the AND operator between them.
func And(predicates ...predicate.Novel) predicate.Novel {
	return predicate.Novel(func(s *sql.Selector) {
		s1 := s.Clone().SetP(nil)
		for _, p := range predicates {
			p(s1)
		}
		s.Where(s1.P())
	})
}

// Or groups predicates with the OR operator between them.
func Or(predicates ...predicate.Novel) predicate.Novel {
	return predicate.Novel(func(s *sql.Selector) {
		s1 := s.Clone().SetP(nil)
		for i, p := range predicates {
			if i > 0 {
				s1.Or()
			}
			p(s1)
		}
		s.Where(s1.P())
	})
}

// Not applies the not operator on the given predicate.
func Not(p predicate.Novel) predicate.Novel {
	return predicate.Novel(func(s *sql.Selector) {
		p(s.Not())
	})
}
